      if (!document.fonts.check('700 24px / 36px Roboto, Oxygen-Sans, Ubuntu, Cantarell, "Helvetica Neue", sans-serif', letter)) return false;
      let can;
      if (window.OffscreenCanvas) {
        can = new OffscreenCanvas(50, 50);
      } else {
