      ctx.fillText("·øø", 10, 10);
      const imgData2 = ctx.getImageData(0, 0, 50, 50);
      // drawing of character must differ from drawing of known invalid character
      return !imgData1.data.every((x, i) => imgData2.data[i] === x);
    }

